-- =============================================
-- Author:		<Golchoobian>
-- Create date: <01/05/2019>
-- Version:		<3.0.0.0>
-- Description:	<Calculate CRC-32 Value from input string>
-- Input Parameters:
--	@input:		//Any input data can be casted to nvarchar(max)
-- =============================================
CREATE FUNCTION [dbo].[dbafn_crc32](
	@input NVARCHAR(MAX)
	)
RETURNS VARBINARY(4)
AS
BEGIN
	DECLARE @myInput NVARCHAR(MAX);
	DECLARE @myCRC BIGINT
	DECLARE @myLookup varbinary(2048)
	DECLARE @myNumList TABLE (RowId INT IDENTITY,Flag BIT)
	DECLARE @myNumListCount INT

	SET @myInput = CAST(@input AS NVARCHAR(MAX))
	SET @myCRC  = 0xFFFFFFFF
	SET @myLookup = ;
	SET @myNumListCount=0;

	WHILE LEN(@myInput)-@myNumListCount>=100
	BEGIN
		INSERT INTO @myNumList ([Flag]) SELECT TOP 100 1 FROM sys.[all_objects]
		SET @myNumListCount=@myNumListCount+100
	END

	WHILE @myNumListCount<LEN(@myInput)
	BEGIN
		INSERT INTO @myNumList ([Flag]) VALUES (1)
		SET @myNumListCount=@myNumListCount+1
	END

	SELECT @myCRC = (@myCRC / 256) ^ Substring(@myLookup, ((@myCRC & 0xFF) ^ Ascii(Substring(@myInput, [@myNumList].[RowId], 1))) * 4 + 1, 4)
	FROM @myNumList

	SET @myCRC = ~@myCRC;
	RETURN Convert(VARBINARY(4), @myCRC)
	--SELECT @myCRC AS CRC32, Convert(VARBINARY(4), @myCRC) AS CRC32Hex;
END


GO
